import React from "react";
import { differenceInDays, formatDistanceToNow} from 'date-fns'
import { tr } from "date-fns/locale";

const Task = ({ taskObj, onComplete }) => {

  const distance = formatDistanceToNow(
    new Date(taskObj.deadline),
    {addSuffix: true, 
     locale: tr
    }, 
  );

  const isDeadlineClose =  differenceInDays(
    new Date(taskObj.deadline),
    new Date()
  ) <= 3;

  return (
    <div className="p-6 bg-[#fff] rounded-[5px] mt-4 shadow-[0_4px_5px_0_rgba(0,0,0,0.1)]  ">
      <h3>{taskObj.title}</h3>
      <div className="deadline">son teslim: <span className={isDeadlineClose? "bg-[#ffd9d4]" : "bg-[#b3b6e3]"}>{distance}</span></div>
      <p>{taskObj.description}</p>
      <div>
        {taskObj.people.map((p) => (
          <span className="pill" key={p}>{p}</span>
        ))}
      </div>
      {onComplete && <button onClick={() => onComplete(taskObj.id)}>TamamlandÄ±</button>}
    </div>
  );
};

export default Task;
